<?phpfunction getcurrentDate(){	$today =  getdate();	$date = ($today["mday"]."-".$today["mon"]."-".$today["year"]);	return $date;}function convertdateToInt($date){	$sum = 0;	$day = substr(0,strpos($date,"-"));	$month = substr(strpos($date,"-")+1, strpos($date,"-") +3);	$year = substr(strpos($date,"-")+4,strpos($date,"-")+8);	echo $day." ".$month." ".$year;}function delrecordById($tbl_name, $id){	$query = "DELETE FROM {$tbl_name} WHERE id={$id}";	return mysql_query($query);}function getrecordById($tbl_name, $id){	$query = "SELECT * FROM {$tbl_name} WHERE id={$id}";	return mysql_query($query);}function getrecordByDate($tbl_name, $date){	$query = "SELECT * FROM {$tbl_name} WHERE update='{$date}'";	return mysql_query($query);}function countrecord($tbl_name, $tbl_comlumn, $compare){	$query = "SELECT COUNT(*) AS num FROM {$tbl_name} WHERE {$tbl_comlumn} = '{$compare}'";	$rs = mysql_query($query);	$num = $rs["num"];	return $num;}function pagenav($base_url, $start,$name, $max_value, $num_per_page) {	//Chuyen Dao Tao PHP & MySQL thuc te, chuyen nghiep tai Ha Noi.	//Pham Hung Thang	//DT: 0982.858506	//YM: hungthangitv	//Email: hungthangitvn@gmail.com	//Website: www.htvsite.com	/*	 *Phan trang cho trang web	 */	$pgcont = 4;	$pgcont = (int) ($pgcont - ($pgcont % 2)) / 2;	if ($start >= $max_value)		$start = max(0, (int) $max_value - (((int) $max_value % (int) $num_per_page) == 0 ? $num_per_page : ((int) $max_value % (int) $num_per_page)));	else		$start = max(0, (int) $start - ((int) $start % (int) $num_per_page));	$base_link = '<a class="navpg" href="' . strtr($base_url, array('%' => '%%')) . "{$name}=%d" . '">%s</a> ';	$pageindex = $start == 0 ? '' : sprintf($base_link, $start - $num_per_page, '&lt;');	if ($start > $num_per_page * $pgcont)		$pageindex .= sprintf($base_link, 0, '1');	if ($start > $num_per_page * ($pgcont + 1))		$pageindex .= '<span style="font-weight: bold;"> ... </span>';	for ($nCont = $pgcont; $nCont >= 1; $nCont--)	if ($start >= $num_per_page * $nCont) {		$tmpStart = $start - $num_per_page * $nCont;		$pageindex .= sprintf($base_link, $tmpStart, $tmpStart / $num_per_page + 1);	}	$pageindex .= '[<b>' . ($start / $num_per_page + 1) . '</b>] ';	$tmpMaxPages = (int) (($max_value - 1) / $num_per_page) * $num_per_page;	for ($nCont = 1; $nCont <= $pgcont; $nCont++)	if ($start + $num_per_page * $nCont <= $tmpMaxPages) {		$tmpStart = $start + $num_per_page * $nCont;		$pageindex .= sprintf($base_link, $tmpStart, $tmpStart / $num_per_page + 1);	}	if ($start + $num_per_page * ($pgcont + 1) < $tmpMaxPages)		$pageindex .= '<span style="font-weight: bold;"> ... </span>';	if ($start + $num_per_page * $pgcont < $tmpMaxPages)		$pageindex .= sprintf($base_link, $tmpMaxPages, $tmpMaxPages / $num_per_page + 1);	if ($start + $num_per_page < $max_value) {		$display_page = ($start + $num_per_page) > $max_value ? $max_value : ($start + $num_per_page);		$pageindex .= sprintf($base_link, $display_page, '&gt;');	}	return $pageindex;}function uploadImg(){	$allowedExts = array("gif", "jpeg", "jpg", "png");	$temp = explode(".", $_FILES["file"]["name"]);	$extension = end($temp);	if ((($_FILES["file"]["type"] == "image/gif")			|| ($_FILES["file"]["type"] == "image/jpeg")			|| ($_FILES["file"]["type"] == "image/jpg")			|| ($_FILES["file"]["type"] == "image/pjpeg")			|| ($_FILES["file"]["type"] == "image/x-png")			|| ($_FILES["file"]["type"] == "image/png"))			&& in_array($extension, $allowedExts))	{		if ($_FILES["file"]["error"] > 0){			$msg="<script> alert('Tải lên file lỗi!');"."</script>";		}		else{			/* $msg="<script> alert(Tải lên file '". $_FILES["file"]["name"]." thành công!');"."</script>"; */				if (file_exists("upload/" . $_FILES["file"]["name"]))			{				$msg = "<script>". $_FILES["file"]["name"] . " đã tồn tại!"."</script>";			}			else			{				move_uploaded_file($_FILES["file"]["tmp_name"],				"upload/" . $_FILES["file"]["name"]);				echo "Stored in: " . "upload/" . $_FILES["file"]["name"];				$msg="<script> alert(Tải lên file '". $_FILES["file"]["name"]." thành công!');"."</script>";			}		}	}	else	{		$msg = "<script>"."alert('Sai định dạng file!')"."</script>";	}	echo $msg;	return "upload/" . $_FILES["file"]["name"];}function contain($element,$array){	for($i=0; $i < sizeof($array); $i++)		if($array[$i] == $element)			return true;	return false;	}?>